package com.basic.example;

public class ExampleTypeConversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int i;
			double f;
			
			f =5/4;
			System.out.println("f :"+f);
			f =(double)5/4;
			System.out.println("f(double):"+f);
			
			i = (int)1.3+(int)2.8;
			System.out.println(i);
			
			
	}

}
